mapper.select:
SELECT SEQ, ID, PWD, EMAIL, POINT, NICKNAME, PIC, INTRO, AUTH
FROM DDOTMEMBER
WHERE ID=#{id} AND PWD=#{pwd}

SELECT NVL(COUNT(*), 0)
FROM DDOTMEMBER
WHERE ID=#{id}

SELECT NVL(COUNT(*), 0)
FROM DDOTMEMBER
WHERE NICKNAME=#{nickname}

mapper.update:
UPDATE DDOTMEMBER 
SET PWD=#{pwd}, EMAIL=#{email}, NICKNAME=#{nickname}, PIC=#{pic}, INTRO=#{intro}
WHERE ID=#{id}

UPDATE DDOTMEMBER 
SET PWD=#{pwd}, EMAIL=#{email}, NICKNAME=#{nickname}, INTRO=#{intro}
WHERE ID=#{id}
