mapper.select:
	SELECT SEQ, NICKNAME, SENDTO, CONTENT, WDATE, READ, DEL
	FROM 
	( SELECT ROW_NUMBER() OVER (ORDER BY  WDATE DESC) as rnum,  
	SEQ, NICKNAME, SENDTO, CONTENT, WDATE, READ, DEL FROM DDOTMESSAGE
    WHERE 
    SENDTO = #{sendto,jdbcType=VARCHAR} and DEL = 2
	NICKNAME = #{nickname,jdbcType=VARCHAR}
	SENDTO = #{sendto,jdbcType=VARCHAR} and DEL = 0
	) A
	WHERE rnum between ${start} AND ${end}

SELECT SEQ, NICKNAME, SENDTO, CONTENT, WDATE, READ, DEL
FROM DDOTMESSAGE
WHERE SEQ=#{seq}

mapper.update:
UPDATE DDOTMESSAGE
SET DEL=0
WHERE SEQ=#{seq}

UPDATE DDOTMESSAGE
SET READ=1
WHERE SEQ=#{seq}
